mapper.select:
		SELECT count(*) FROM(
			SELECT * FROM product
			WHERE blind_code='show' AND purpose_code='sale' AND book_category=#{value}
		)countable

SELECT * FROM( SELECT DISTINCT p.prod_name, p.author, p.retail_price, p.prod_no, h.hashtag_name, p.prod_type, p.blind_code, p.purpose_code, p.reg_date FROM product p LEFT OUTER JOIN hashtag h ON p.prod_no = h.prod_no
author LIKE '%${search.searchKeyword}%'
prod_name LIKE '%${search.searchKeyword}%'
h.hashtag_name LIKE '%${search.searchKeyword}%'
) pp
WHERE (prod_type = 'paper' OR prod_type = 'ebook') AND blind_code='show' AND purpose_code='sale' GROUP BY pp.prod_no ORDER BY reg_date DESC LIMIT #{search.pageSize} OFFSET #{offset}


SELECT * 
FROM product 
WHERE prod_type = #{prodType}
ORDER BY reg_date DESC
LIMIT #{search.pageSize} OFFSET #{offset}

			SELECT count(*) FROM(
