				WHERE blind_code='show' AND prod_type=#{value}
			)countable

SELECT * FROM( SELECT DISTINCT p.prod_name, p.author, p.retail_price, p.prod_no, h.hashtag_name, p.prod_type, p.blind_code, p.purpose_code, p.reg_date FROM product p LEFT OUTER JOIN hashtag h ON p.prod_no = h.prod_no
author LIKE '%${search.searchKeyword}%'
prod_name LIKE '%${search.searchKeyword}%'
h.hashtag_name LIKE '%${search.searchKeyword}%'
) pp
WHERE prod_type = #{prodType} AND blind_code='show' AND purpose_code='sale' ORDER BY reg_date DESC LIMIT #{search.pageSize} OFFSET #{offset}

mapper.delete:
				DELETE
				FROM wish
				WHERE prod_no = #{prodNo} AND user_id = #{userId}

mapper.select:

				SELECT wish_no
				FROM wish
				WHERE prod_no = #{prodNo} AND user_id = #{userId}

select count(*) FROM(
	SELECT wish_no
	FROM wish
	WHERE user_id = #{userId}
)countable

mapper.update:
UPDATE buy
SET buy_amount = #{updatedAmount}			
WHERE prod_no = #{prodNo} AND user_id = #{userId} AND buy_code = 'ready'

mapper.select:
				SELECT prod_no
				FROM buy
				WHERE buy_no = #{buyNo}

mapper.update:
UPDATE review
SET review_image = #{reviewImage}			
WHERE buy_no = #{buyNo}

UPDATE review
SET review_content = #{reviewContent}, star_rate = #{starRate}			
WHERE buy_no = #{buyNo}

mapper.select:
SELECT * FROM(
			SELECT r.review_no, r.user_id, r.buy_no, r.review_name, r.review_content, r.review_image, r.star_rate, r.reg_date, u.nickname, u.profile
			FROM review r
			INNER JOIN user u
			ON r.user_id = u.user_id
			WHERE prod_no = #{prodNo}
			) pp
			ORDER BY reg_date DESC
			LIMIT #{pageSize} OFFSET #{offset}
